        public static void ProcessInputA(string puzzle)
        {
            var floor = 0;
            var i = 0;

            foreach (var ch in puzzle)
            {
                i++;
                if (ch == '(')
                    floor++;
                else if (ch == ')')
                    floor--;
            }

            Console.WriteLine("Santa's on floor# " + floor);
        }

        public static void ProcessInputB(string puzzle)
