            var floor = 0;
            var i = 0;

            foreach (var ch in puzzle)
            {
                i++;
                if (ch == '(')
                    floor++;
                else if (ch == ')')
                    floor--;

                if (floor == -1)
                {
                    Console.WriteLine("Basement at index " + i + "!!");
                    break;
                }
            }

